import React, { useState, useEffect } from 'react';
import { 
  UsersIcon, 
  BuildingOfficeIcon, 
  ChartBarIcon,
  PlusIcon,
  PencilIcon,
  TrashIcon,
  EyeIcon,
  CheckCircleIcon,
  XCircleIcon,
  XMarkIcon,
  ArrowPathIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../contexts/AuthContext';

interface Usuario {
  id: number;
  nome: string;
  email: string;
  cargo: string;
  setor: string;
  ativo: boolean;
  admin: boolean;
  createdAt: string;
}

interface Nivel {
  id: number;
  nome: string;
  prioridade: number;
  podeCriarOrcamento: boolean;
  podeAprovar: boolean;
  nivelFinal: boolean;
  ativo: boolean;
}

interface Filial {
  id: number;
  nome: string;
  endereco?: string;
  telefone?: string;
  ativo: boolean;
}

interface DashboardStats {
  totalUsuarios: number;
  usuariosAtivos: number;
  totalNiveis: number;
  niveisAtivos: number;
  totalFiliais: number;
  filiaisAtivas: number;
}

// Modal reutilizável para cadastro/edição de usuário
interface UserModalProps {
  open: boolean;
  onClose: () => void;
  onSave: (form: any) => void;
  initialData: any;
  niveis: Nivel[];
}

interface NivelModalProps {
  open: boolean;
  onClose: () => void;
  onSave: (form: any) => void;
  initialData: any;
}

const UserModal: React.FC<UserModalProps> = ({ open, onClose, onSave, initialData, niveis }) => {
  const [form, setForm] = useState<{
    nome: string;
    email: string;
    senha: string;
    cargo: string;
    setor: string;
    admin: boolean;
    ativo: boolean;
    niveis: number[];
  }>({
    nome: '',
    email: '',
    senha: '',
    cargo: '',
    setor: '',
    admin: false,
    ativo: true,
    niveis: [],
  });
  useEffect(() => {
    if (initialData) {
      setForm({
        nome: initialData.nome || '',
        email: initialData.email || '',
        senha: '',
        cargo: initialData.cargo || '',
        setor: initialData.setor || '',
        admin: initialData.admin || false,
        ativo: initialData.ativo ?? true,
        niveis: initialData.usuarioNiveis ? initialData.usuarioNiveis.map((n: any) => n.nivel.id) : [],
      });
    } else {
      setForm({ nome: '', email: '', senha: '', cargo: '', setor: '', admin: false, ativo: true, niveis: [] });
    }
  }, [initialData, open]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleNivelChange = (nivelId: number) => {
    setForm((prev) => {
      const niveis = prev.niveis.includes(nivelId)
        ? prev.niveis.filter((id) => id !== nivelId)
        : [...prev.niveis, nivelId];
      return { ...prev, niveis };
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSave(form);
  };

  if (!open) return null;
  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-40">
      <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md relative">
        <button onClick={onClose} className="absolute top-2 right-2 text-gray-400 hover:text-gray-700">
          <span className="text-2xl">&times;</span>
        </button>
        <h2 className="text-xl font-bold mb-4">{initialData ? 'Editar Usuário' : 'Novo Usuário'}</h2>
        <form onSubmit={handleSubmit} className="space-y-3">
          <input name="nome" value={form.nome} onChange={handleChange} placeholder="Nome" className="w-full border rounded p-2" required />
          <input name="email" value={form.email} onChange={handleChange} placeholder="Email" className="w-full border rounded p-2" type="email" required />
          <input name="senha" value={form.senha} onChange={handleChange} placeholder="Senha" className="w-full border rounded p-2" type="password" autoComplete="new-password" required={!initialData} />
          <input name="cargo" value={form.cargo} onChange={handleChange} placeholder="Cargo" className="w-full border rounded p-2" required />
          <input name="setor" value={form.setor} onChange={handleChange} placeholder="Setor" className="w-full border rounded p-2" required />
          <label className="flex items-center gap-2">
            <input name="admin" type="checkbox" checked={form.admin} onChange={handleChange} />
            Administrador
          </label>
          <label className="flex items-center gap-2">
            <input name="ativo" type="checkbox" checked={form.ativo} onChange={handleChange} />
            Ativo
          </label>
          <div>
            <div className="font-medium mb-1">Níveis de aprovação:</div>
            <div className="flex flex-wrap gap-2">
              {niveis.map((nivel) => (
                <label key={nivel.id} className="flex items-center gap-1 text-xs border rounded px-2 py-1">
                  <input type="checkbox" checked={form.niveis.includes(nivel.id)} onChange={() => handleNivelChange(nivel.id)} />
                  {nivel.nome}
                </label>
              ))}
            </div>
          </div>
          <button type="submit" className="w-full bg-blue-600 text-white rounded p-2 hover:bg-blue-700">Salvar</button>
        </form>
      </div>
    </div>
  );
};

const NivelModal: React.FC<NivelModalProps> = ({ open, onClose, onSave, initialData }) => {
  const [form, setForm] = useState<{
    nome: string;
    prioridade: number;
    podeCriarOrcamento: boolean;
    podeAprovar: boolean;
    nivelFinal: boolean;
    ativo: boolean;
  }>({
    nome: '',
    prioridade: 1,
    podeCriarOrcamento: true,
    podeAprovar: false,
    nivelFinal: false,
    ativo: true,
  });

  useEffect(() => {
    if (initialData) {
      setForm({
        nome: initialData.nome || '',
        prioridade: initialData.prioridade || 1,
        podeCriarOrcamento: initialData.podeCriarOrcamento ?? true,
        podeAprovar: initialData.podeAprovar ?? false,
        nivelFinal: initialData.nivelFinal ?? false,
        ativo: initialData.ativo ?? true,
      });
    } else {
      setForm({
        nome: '',
        prioridade: 1,
        podeCriarOrcamento: true,
        podeAprovar: false,
        nivelFinal: false,
        ativo: true,
      });
    }
  }, [initialData, open]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : type === 'number' ? parseInt(value) : value,
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSave(form);
  };

  if (!open) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-md">
        <h3 className="text-lg font-medium mb-4">
          {initialData ? 'Editar Nível' : 'Novo Nível'}
        </h3>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Nome
            </label>
            <input
              type="text"
              name="nome"
              value={form.nome}
              onChange={handleChange}
              className="w-full border border-gray-300 rounded px-3 py-2"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Prioridade
            </label>
            <input
              type="number"
              name="prioridade"
              value={form.prioridade}
              onChange={handleChange}
              min="1"
              className="w-full border border-gray-300 rounded px-3 py-2"
              required
            />
          </div>
          <div className="space-y-2">
            <label className="flex items-center">
              <input
                type="checkbox"
                name="podeCriarOrcamento"
                checked={form.podeCriarOrcamento}
                onChange={handleChange}
                className="mr-2"
              />
              <span className="text-sm">Pode criar orçamentos</span>
            </label>
            <label className="flex items-center">
              <input
                type="checkbox"
                name="podeAprovar"
                checked={form.podeAprovar}
                onChange={handleChange}
                className="mr-2"
              />
              <span className="text-sm">Pode aprovar orçamentos</span>
            </label>
            <label className="flex items-center">
              <input
                type="checkbox"
                name="nivelFinal"
                checked={form.nivelFinal}
                onChange={handleChange}
                className="mr-2"
              />
              <span className="text-sm">Nível final de aprovação</span>
            </label>
            <label className="flex items-center">
              <input
                type="checkbox"
                name="ativo"
                checked={form.ativo}
                onChange={handleChange}
                className="mr-2"
              />
              <span className="text-sm">Ativo</span>
            </label>
          </div>
          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-gray-600 border border-gray-300 rounded hover:bg-gray-50"
            >
              Cancelar
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Salvar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const Admin: React.FC = () => {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState('dashboard');
  const [loading, setLoading] = useState(true);
  const [usuarios, setUsuarios] = useState<Usuario[]>([]);
  const [niveis, setNiveis] = useState<Nivel[]>([]);
  const [filiais, setFiliais] = useState<Filial[]>([]);
  const [stats, setStats] = useState<DashboardStats>({
    totalUsuarios: 0,
    usuariosAtivos: 0,
    totalNiveis: 0,
    niveisAtivos: 0,
    totalFiliais: 0,
    filiaisAtivas: 0
  });
  const [fluxoModal, setFluxoModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState<Usuario | null>(null);
  const [userFluxo, setUserFluxo] = useState<any[]>([]);
  const [availableNiveis, setAvailableNiveis] = useState<Nivel[]>([]);
  const [userModalOpen, setUserModalOpen] = useState(false);
  const [editingUser, setEditingUser] = useState<Usuario | null>(null);
  const [nivelModalOpen, setNivelModalOpen] = useState(false);
  const [editingNivel, setEditingNivel] = useState<Nivel | null>(null);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');

      // Buscar dados em paralelo
      const [usuariosRes, niveisRes, filiaisRes] = await Promise.all([
        fetch('http://localhost:5000/api/users', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch('http://localhost:5000/api/niveis', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch('http://localhost:5000/api/filiais', {
          headers: { 'Authorization': `Bearer ${token}` }
        })
      ]);

      if (usuariosRes.ok) {
        const usuariosData = await usuariosRes.json();
        setUsuarios(usuariosData);
      }

      if (niveisRes.ok) {
        const niveisData = await niveisRes.json();
        setNiveis(niveisData);
      }

      if (filiaisRes.ok) {
        const filiaisData = await filiaisRes.json();
        setFiliais(filiaisData);
      }

      // Calcular estatísticas
      setStats({
        totalUsuarios: usuarios.length,
        usuariosAtivos: usuarios.filter(u => u.ativo).length,
        totalNiveis: niveis.length,
        niveisAtivos: niveis.filter(n => n.ativo).length,
        totalFiliais: filiais.length,
        filiaisAtivas: filiais.filter(f => f.ativo).length
      });

    } catch (error) {
      console.error('Erro ao carregar dados:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleToggleStatus = async (type: 'usuario' | 'nivel' | 'filial', id: number, currentStatus: boolean) => {
    try {
      const token = localStorage.getItem('token');
      const endpoint = type === 'usuario' ? 'users' : type === 'nivel' ? 'niveis' : 'filiais';

      const response = await fetch(`http://localhost:5000/api/${endpoint}/${id}`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ ativo: !currentStatus })
      });

      if (response.ok) {
        fetchData(); // Recarregar dados
      }
    } catch (error) {
      console.error(`Erro ao alterar status do ${type}:`, error);
    }
  };

  const handleDelete = async (type: 'usuario' | 'nivel' | 'filial', id: number) => {
    if (!window.confirm(`Tem certeza que deseja excluir este ${type}?`)) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const endpoint = type === 'usuario' ? 'users' : type === 'nivel' ? 'niveis' : 'filiais';

      const response = await fetch(`http://localhost:5000/api/${endpoint}/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        fetchData(); // Recarregar dados
      }
    } catch (error) {
      console.error(`Erro ao excluir ${type}:`, error);
    }
  };

  // Carregar fluxo de um usuário
  const loadUserFluxo = async (userId: number) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/users/${userId}/fluxo`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const fluxo = await response.json();
        setUserFluxo(fluxo);
      } else {
        setUserFluxo([]);
      }
    } catch (error) {
      console.error('Erro ao carregar fluxo:', error);
      setUserFluxo([]);
    }
  };

  // Salvar fluxo de um usuário
  const saveUserFluxo = async () => {
    if (!selectedUser) return;

    try {
      const token = localStorage.getItem('token');
      const niveis = userFluxo.map(item => item.nivelId || item.nivel.id);

      const response = await fetch(`http://localhost:5000/api/users/${selectedUser.id}/fluxo`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ niveis }),
      });

      if (response.ok) {
        const fluxo = await response.json();
        setUserFluxo(fluxo);
        alert('Fluxo salvo com sucesso!');
      } else {
        const error = await response.json();
        alert(`Erro ao salvar fluxo: ${error.error}`);
      }
    } catch (error) {
      console.error('Erro ao salvar fluxo:', error);
      alert('Erro ao salvar fluxo');
    }
