import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  ArrowLeftIcon,
  DocumentTextIcon,
  CurrencyDollarIcon,
  BuildingOfficeIcon,
  UserIcon,
  LinkIcon,
  PhotoIcon,
  PaperClipIcon,
  CheckCircleIcon,
  XCircleIcon,
  ClockIcon,
  EyeIcon,
  ChartBarIcon,
  ShoppingCartIcon,
  ShoppingBagIcon,
  TruckIcon,
  CheckBadgeIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../contexts/AuthContext';

interface Orcamento {
  id: number;
  titulo: string;
  descricao: string;
  valor: number;
  quantidade: number;
  fornecedor?: string;
  links?: string;
  fotos?: string;
  anexos?: string;
  status: string;
  observacoes?: string;
  createdAt: string;
  updatedAt: string;
  solicitante: {
    id: number;
    nome: string;
    email: string;
    cargo: string;
    setor: string;
  };
  nivelAtual: {
    id: number;
    nome: string;
    prioridade: number;
  };
  filial: {
    id: number;
    nome: string;
    endereco?: string;
  };
  aprovacoes: Array<{
    id: number;
    observacoes?: string;
    createdAt: string;
    aprovador: {
      nome: string;
      cargo: string;
    };
  }>;
  rejeicoes: Array<{
    id: number;
    motivo: string;
    observacoes?: string;
    createdAt: string;
    rejeitador: {
      nome: string;
      cargo: string;
    };
  }>;
}

const OrcamentoDetalhes: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [orcamento, setOrcamento] = useState<Orcamento | null>(null);
  const [loading, setLoading] = useState(true);
  const [showAprovar, setShowAprovar] = useState(false);
  const [showRejeitar, setShowRejeitar] = useState(false);
  const [obsAprovacao, setObsAprovacao] = useState('');
  const [motivoRejeicao, setMotivoRejeicao] = useState('');
  const [obsRejeicao, setObsRejeicao] = useState('');
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    if (id) {
      fetchOrcamento();
    }
  }, [id]);

  const fetchOrcamento = async () => {
    if (submitting) return; // N칚o buscar se estiver submetendo

    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/orcamentos/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setOrcamento(data);
      } else {
        console.error('Erro ao buscar or칞amento');
      }
    } catch (error) {
      console.error('Erro ao buscar or칞amento:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'PENDENTE': return 'bg-yellow-100 text-yellow-800';
      case 'APROVADO': return 'bg-green-100 text-green-800';
      case 'REJEITADO': return 'bg-red-100 text-red-800';
      case 'AGUARDANDO_COMPRA': return 'bg-blue-100 text-blue-800';
      case 'COMPRADO': return 'bg-purple-100 text-purple-800';
      case 'COMPRA_EFETUADA': return 'bg-indigo-100 text-indigo-800';
      case 'FINALIZADO': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'PENDENTE': return <ClockIcon className="h-4 w-4" />;
      case 'APROVADO': return <CheckCircleIcon className="h-4 w-4" />;
      case 'REJEITADO': return <XCircleIcon className="h-4 w-4" />;
      case 'AGUARDANDO_COMPRA': return <ShoppingCartIcon className="h-4 w-4" />;
      case 'COMPRADO': return <ShoppingBagIcon className="h-4 w-4" />;
      case 'COMPRA_EFETUADA': return <TruckIcon className="h-4 w-4" />;
      case 'FINALIZADO': return <CheckBadgeIcon className="h-4 w-4" />;
      default: return <DocumentTextIcon className="h-4 w-4" />;
    }
  };

  // Verifica se o usu치rio pode aprovar/rejeitar
  const podeAprovarOuRejeitar =
    user &&
    user.usuarioNiveis &&
    orcamento &&
    orcamento.status === 'PENDENTE' &&
    user.usuarioNiveis.some(un => un.nivel.podeAprovar && un.nivel.id === orcamento.nivelAtual.id);

  // Verifica se o usu치rio pode marcar como comprado (n칤vel final/compras)
  const podeMarcarComoComprado =
    user &&
    user.usuarioNiveis &&
    orcamento &&
    (orcamento.status === 'APROVADO' || orcamento.status === 'AGUARDANDO_COMPRA') &&
    user.usuarioNiveis.some(un => un.nivel.nivelFinal && un.nivel.id === orcamento.nivelAtual.id);

  // Fun칞칚o para aprovar or칞amento
  const handleAprovar = async () => {
    // Definir submitting imediatamente para evitar qualquer renderiza칞칚o
    setSubmitting(true);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/orcamentos/${orcamento?.id}/aprovar`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ observacao: obsAprovacao || '' })
      });

      if (response.ok) {
        alert('Or칞amento aprovado com sucesso!');
        // Redirecionar usando navigate para manter autentica칞칚o
        navigate('/dashboard', { replace: true });
        return;
      } else {
        const errorData = await response.json();
        alert(errorData.error || 'Erro ao aprovar or칞amento');
        setSubmitting(false); // S칩 resetar se der erro
      }
    } catch (error) {
      console.error('Erro ao aprovar or칞amento:', error);
      alert('Erro ao aprovar or칞amento');
      setSubmitting(false); // S칩 resetar se der erro
    }
  };

  // Fun칞칚o para rejeitar or칞amento
  const handleRejeitar = async () => {
    // Definir submitting imediatamente para evitar qualquer renderiza칞칚o
    setSubmitting(true);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/orcamentos/${orcamento?.id}/rejeitar`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ observacao: obsRejeicao || '' })
      });

      if (response.ok) {
        alert('Or칞amento rejeitado com sucesso!');
        // Redirecionar usando navigate para manter autentica칞칚o
        navigate('/dashboard', { replace: true });
        return;
      } else {
        const errorData = await response.json();
        alert(errorData.error || 'Erro ao rejeitar or칞amento');
        setSubmitting(false); // S칩 resetar se der erro
      }
    } catch (error) {
      console.error('Erro ao rejeitar or칞amento:', error);
      alert('Erro ao rejeitar or칞amento');
      setSubmitting(false); // S칩 resetar se der erro
    }
  };

  // Fun칞칚o para marcar como comprado
  const handleMarcarComoComprado = async () => {
    if (!window.confirm('Tem certeza que deseja marcar este or칞amento como comprado?')) {
      return;
    }

    // Definir submitting imediatamente para evitar qualquer renderiza칞칚o
    setSubmitting(true);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/orcamentos/${orcamento?.id}/comprado`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      if (response.ok) {
        alert('Or칞amento marcado como comprado com sucesso!');
        // Redirecionar usando navigate para manter autentica칞칚o
        navigate('/dashboard', { replace: true });
        return;
      } else {
        const errorData = await response.json();
        alert(errorData.error || 'Erro ao marcar como comprado');
        setSubmitting(false); // S칩 resetar se der erro
      }
    } catch (error) {
      console.error('Erro ao marcar como comprado:', error);
      alert('Erro ao marcar como comprado');
      setSubmitting(false); // S칩 resetar se der erro
    }
  };

  // Fun칞칚o para dar baixa no or칞amento
  const handleDarBaixa = async () => {
    if (!window.confirm('Tem certeza que deseja dar baixa neste or칞amento? O item chegou?')) {
      return;
    }

    // Definir submitting imediatamente para evitar qualquer renderiza칞칚o
    setSubmitting(true);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/orcamentos/${orcamento?.id}/baixa`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      if (response.ok) {
        alert('Baixa realizada com sucesso! Or칞amento finalizado.');
        // Redirecionar usando navigate para manter autentica칞칚o
        navigate('/dashboard', { replace: true });
        return;
      } else {
        const errorData = await response.json();
        alert(errorData.error || 'Erro ao dar baixa');
        setSubmitting(false);
      }
    } catch (error) {
      console.error('Erro ao dar baixa:', error);
      alert('Erro ao dar baixa no or칞amento');
      setSubmitting(false);
    }
  };

  // Se estiver submetendo, n칚o renderizar nada
  if (submitting) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Processando...</p>
        </div>
      </div>
    );
  }

  // Se estiver carregando, mostrar loader
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Carregando or칞amento...</p>
        </div>
      </div>
    );
  }

  if (!orcamento) {
    return (
      <div className="text-center py-12">
        <p className="text-gray-500">Or칞amento n칚o encontrado</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow p-6">
