import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  ArrowLeftIcon,
  DocumentTextIcon,
  CurrencyDollarIcon,
  BuildingOfficeIcon,
  UserIcon,
  LinkIcon,
  PhotoIcon,
  PaperClipIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../contexts/AuthContext';
import Notification from '../components/Notification';

interface Filial {
  id: number;
  nome: string;
}

interface Orcamento {
  id: number;
  titulo: string;
  descricao: string;
  valor: number;
  quantidade: number;
  fornecedor?: string;
  links?: string;
  fotos?: string;
  anexos?: string;
  observacoes?: string;
  status: string;
  filial: {
    id: number;
    nome: string;
  };
}

interface UploadedFile {
  file: File;
  preview: string;
  name: string;
}

const EditarOrcamento: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [filiais, setFiliais] = useState<Filial[]>([]);
  const [orcamento, setOrcamento] = useState<Orcamento | null>(null);
  const [uploadedFiles, setUploadedFiles] = useState<UploadedFile[]>([]);
  const [uploadedDocuments, setUploadedDocuments] = useState<UploadedFile[]>([]);
  const [notification, setNotification] = useState<{
    type: 'success' | 'error';
    message: string;
    isVisible: boolean;
  }>({
    type: 'success',
    message: '',
    isVisible: false
  });

  const [formData, setFormData] = useState({
    titulo: '',
    descricao: '',
    valor: '',
    quantidade: '1',
    fornecedor: '',
    links: '',
    anexos: '',
    observacoes: '',
    filialId: ''
  });

  const [errors, setErrors] = useState<Record<string, string>>({});
  const [valorTotal, setValorTotal] = useState('0,00');

  useEffect(() => {
    if (id) {
      fetchOrcamento();
      fetchFiliais();
    }
  }, [id]);

  useEffect(() => {
    let valor = parseFloat(formData.valor.replace(',', '.') || '0');
    let quantidade = parseInt(formData.quantidade || '0');
    if (isNaN(valor)) valor = 0;
    if (isNaN(quantidade)) quantidade = 0;
    const total = valor * quantidade;
    setValorTotal(total.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 }));
  }, [formData.valor, formData.quantidade]);

  const fetchOrcamento = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/orcamentos/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setOrcamento(data);

        // Preencher o formulário com os dados do orçamento
        setFormData({
          titulo: data.titulo,
          descricao: data.descricao,
          valor: data.valor.toString(),
          quantidade: data.quantidade.toString(),
          fornecedor: data.fornecedor || '',
          links: data.links || '',
          anexos: data.anexos || '',
          observacoes: data.observacoes || '',
          filialId: data.filial.id.toString()
        });
      } else {
        alert('Orçamento não encontrado');
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Erro ao carregar orçamento:', error);
      alert('Erro ao carregar orçamento');
      navigate('/dashboard');
    } finally {
      setLoading(false);
    }
  };

  const fetchFiliais = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/filiais', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setFiliais(data);
      }
    } catch (error) {
      console.error('Erro ao carregar filiais:', error);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value, type } = e.target;

    if (name === 'valor') {
      let numericValue = value.replace(/[^\d,]/g, '');
      numericValue = numericValue.replace(/,+/g, ',');
      const commaIndex = numericValue.indexOf(',');
      if (commaIndex !== -1) {
        const beforeComma = numericValue.substring(0, commaIndex);
        const afterComma = numericValue.substring(commaIndex + 1).replace(/,/g, '');
        const limitedAfterComma = afterComma.substring(0, 2);
        numericValue = beforeComma + ',' + limitedAfterComma;
      }
      setFormData(prev => ({
        ...prev,
        [name]: numericValue
      }));
    } else if (name === 'quantidade') {
      // Garante que quantidade nunca fique vazia e só aceita números inteiros positivos
      let quantidade = value.replace(/[^\d]/g, '');
      if (quantidade === '' || quantidade === '0') quantidade = '1';
      setFormData(prev => ({
        ...prev,
        [name]: quantidade
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }

    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>, type: 'fotos' | 'anexos') => {
    const files = Array.from(e.target.files || []);

    files.forEach(file => {
      const uploadedFile: UploadedFile = {
        file,
        preview: URL.createObjectURL(file),
        name: file.name
      };

      if (type === 'fotos') {
        setUploadedFiles(prev => [...prev, uploadedFile]);
      } else {
        setUploadedDocuments(prev => [...prev, uploadedFile]);
      }
    });
  };

  const removeFile = (index: number, type: 'fotos' | 'anexos') => {
    if (type === 'fotos') {
      setUploadedFiles(prev => {
        const newFiles = [...prev];
        URL.revokeObjectURL(newFiles[index].preview);
        newFiles.splice(index, 1);
        return newFiles;
      });
    } else {
      setUploadedDocuments(prev => {
        const newFiles = [...prev];
        URL.revokeObjectURL(newFiles[index].preview);
        newFiles.splice(index, 1);
        return newFiles;
      });
    }
  };

  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    if (!formData.titulo.trim()) {
      newErrors.titulo = 'Título é obrigatório';
    }

    if (!formData.descricao.trim()) {
      newErrors.descricao = 'Descrição é obrigatória';
    }

    if (!formData.valor.trim()) {
      newErrors.valor = 'Valor é obrigatório';
    } else {
      const valor = parseFloat(formData.valor.replace(',', '.'));
      if (isNaN(valor) || valor <= 0) {
        newErrors.valor = 'Valor deve ser um número positivo';
      }
    }

    if (!formData.quantidade.trim()) {
      newErrors.quantidade = 'Quantidade é obrigatória';
    } else {
      const quantidade = parseInt(formData.quantidade);
      if (isNaN(quantidade) || quantidade <= 0) {
        newErrors.quantidade = 'Quantidade deve ser um número positivo';
      }
    }

    if (!formData.filialId) {
      newErrors.filialId = 'Filial é obrigatória';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    // Validação
    const newErrors: Record<string, string> = {};
    if (!formData.titulo.trim()) newErrors.titulo = 'Título é obrigatório';
    if (!formData.descricao.trim()) newErrors.descricao = 'Descrição é obrigatória';
    if (!formData.valor || parseFloat(formData.valor.replace(',', '.')) <= 0) {
      newErrors.valor = 'Valor deve ser maior que zero';
    }
    if (!formData.filialId) newErrors.filialId = 'Filial é obrigatória';
    if (!formData.quantidade || parseInt(formData.quantidade) <= 0) {
      newErrors.quantidade = 'Quantidade deve ser maior que zero';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      setLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const valorNumerico = parseFloat(formData.valor.replace(',', '.'));
      const quantidade = parseInt(formData.quantidade);

      const dataToSend = {
        titulo: formData.titulo,
        descricao: formData.descricao,
        valor: valorNumerico,
        quantidade: quantidade,
        fornecedor: formData.fornecedor,
        links: formData.links,
        observacoes: formData.observacoes
      };

      const response = await fetch(`http://localhost:5000/api/orcamentos/${id}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(dataToSend)
      });

      if (response.ok) {
        setNotification({
          type: 'success',
          message: 'Orçamento atualizado com sucesso!',
          isVisible: true
        });

        // Redirecionar após 2 segundos
        setTimeout(() => {
          navigate('/dashboard');
        }, 2000);
      } else {
        const errorData = await response.json();
        setNotification({
          type: 'error',
          message: errorData.error || errorData.message || 'Erro ao atualizar orçamento',
          isVisible: true
        });
      }
    } catch (error) {
      console.error('Erro ao atualizar orçamento:', error);
      setNotification({
        type: 'error',
        message: 'Erro ao atualizar orçamento. Tente novamente.',
        isVisible: true
      });
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (!orcamento) {
    return (
      <div className="text-center py-12">
        <p className="text-gray-500">Orçamento não encontrado</p>
      </div>
    );
  }

  // Verificar se o orçamento pode ser editado
  if (orcamento.status !== 'PENDENTE') {
    return (
      <div className="space-y-6">
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <button
              onClick={() => navigate('/dashboard')}
              className="mr-4 p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100"
            >
              <ArrowLeftIcon className="h-5 w-5" />
            </button>
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Editar Orçamento #{orcamento.id}</h1>
              <p className="mt-1 text-gray-600">{orcamento.titulo}</p>
            </div>
          </div>
        </div>

        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6">
          <div className="flex">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-yellow-400" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <h3 className="text-sm font-medium text-yellow-800">
                Orçamento não pode ser editado
              </h3>
              <div className="mt-2 text-sm text-yellow-700">
                <p>
