import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import {
  CheckBadgeIcon,
  EyeIcon,
  ClockIcon,
  CheckCircleIcon,
  XCircleIcon,
  ShoppingCartIcon,
  ShoppingBagIcon,
  TruckIcon,
} from '@heroicons/react/24/outline';

interface Orcamento {
  id: number;
  titulo: string;
  descricao: string;
  valor: number;
  quantidade: number;
  status: string;
  createdAt: string;
  solicitante: {
    nome: string;
    cargo: string;
  };
  nivelAtual: {
    id: number;
    nome: string;
  };
  filial: {
    nome: string;
  };
}

const DarBaixa: React.FC = () => {
  const { user } = useAuth();
  const [orcamentos, setOrcamentos] = useState<Orcamento[]>([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState<number | null>(null);

  useEffect(() => {
    fetchOrcamentos();
  }, []);

  const fetchOrcamentos = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/orcamentos', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Todos os orçamentos recebidos:', data);
        console.log('Usuário atual:', user);
        console.log('Usuário tem nível final:', user?.usuarioNiveis?.some(un => un.nivel.nivelFinal));

        // A API retorna { orcamentos: [...], pagination: {...} }
        const orcamentosData = data.orcamentos || data;

        // Filtrar apenas orçamentos com status COMPRA_EFETUADA para usuários com nível final
        const orcamentosFiltrados = orcamentosData.filter((orcamento: Orcamento) => {
          const temStatusCorreto = orcamento.status === 'COMPRA_EFETUADA';
          const usuarioTemNivelFinal = user?.usuarioNiveis?.some(un => un.nivel.nivelFinal);

          console.log(`Orçamento ${orcamento.id}: status=${orcamento.status}, temStatusCorreto=${temStatusCorreto}, usuarioTemNivelFinal=${usuarioTemNivelFinal}`);

          return temStatusCorreto && usuarioTemNivelFinal;
        });

        console.log('Orçamentos filtrados:', orcamentosFiltrados);
        setOrcamentos(orcamentosFiltrados);
      }
    } catch (error) {
      console.error('Erro ao buscar orçamentos:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDarBaixa = async (orcamentoId: number) => {
    if (!window.confirm('Tem certeza que deseja dar baixa neste orçamento? O item chegou?')) {
      return;
    }

    setSubmitting(orcamentoId);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/orcamentos/${orcamentoId}/baixa`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        alert('Baixa realizada com sucesso! Orçamento finalizado.');
        fetchOrcamentos(); // Recarregar a lista
      } else {
        const errorData = await response.json();
        alert(errorData.error || 'Erro ao dar baixa');
      }
    } catch (error) {
      console.error('Erro ao dar baixa:', error);
      alert('Erro ao dar baixa no orçamento');
    } finally {
      setSubmitting(null);
    }
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('pt-BR');
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'PENDENTE': return 'bg-yellow-100 text-yellow-800';
      case 'APROVADO': return 'bg-green-100 text-green-800';
      case 'REJEITADO': return 'bg-red-100 text-red-800';
      case 'AGUARDANDO_COMPRA': return 'bg-blue-100 text-blue-800';
      case 'COMPRADO': return 'bg-purple-100 text-purple-800';
      case 'COMPRA_EFETUADA': return 'bg-indigo-100 text-indigo-800';
      case 'FINALIZADO': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'PENDENTE': return <ClockIcon className="h-4 w-4" />;
      case 'APROVADO': return <CheckCircleIcon className="h-4 w-4" />;
      case 'REJEITADO': return <XCircleIcon className="h-4 w-4" />;
      case 'AGUARDANDO_COMPRA': return <ShoppingCartIcon className="h-4 w-4" />;
      case 'COMPRADO': return <ShoppingBagIcon className="h-4 w-4" />;
      case 'COMPRA_EFETUADA': return <TruckIcon className="h-4 w-4" />;
      case 'FINALIZADO': return <CheckBadgeIcon className="h-4 w-4" />;
      default: return <ClockIcon className="h-4 w-4" />;
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Carregando orçamentos...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow p-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold text-gray-900 flex items-center">
              <CheckBadgeIcon className="h-6 w-6 mr-3 text-indigo-600" />
              Dar Baixa
            </h1>
            <p className="mt-1 text-gray-600">
              Orçamentos com compra efetuada aguardando baixa
            </p>
          </div>
        </div>
      </div>

      {/* Lista de Orçamentos */}
      {orcamentos.length === 0 ? (
        <div className="bg-white rounded-lg shadow p-12 text-center">
          <CheckBadgeIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">Nenhum orçamento para dar baixa</h3>
          <p className="text-gray-500">
            Não há orçamentos com status "Compra Efetuada" aguardando baixa.
          </p>
        </div>
      ) : (
        <div className="bg-white rounded-lg shadow overflow-hidden">
          <div className="px-6 py-4 border-b border-gray-200">
            <h3 className="text-lg font-medium text-gray-900">
              Orçamentos para Dar Baixa ({orcamentos.length})
            </h3>
          </div>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Orçamento
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Solicitante
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Filial
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Valor Total
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Data
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Ações
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {orcamentos.map((orcamento) => (
                  <tr key={orcamento.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div>
                        <div className="text-sm font-medium text-gray-900">
                          #{orcamento.id} - {orcamento.titulo}
                        </div>
                        <div className="text-sm text-gray-500 truncate max-w-xs">
                          {orcamento.descricao}
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">{orcamento.solicitante.nome}</div>
                      <div className="text-sm text-gray-500">{orcamento.solicitante.cargo}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {orcamento.filial.nome}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                      {formatCurrency(orcamento.valor * orcamento.quantidade)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {formatDate(orcamento.createdAt)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(orcamento.status)}`}>
                        {getStatusIcon(orcamento.status)}
                        <span className="ml-1">{orcamento.status}</span>
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2">
                      <Link
                        to={`/orcamentos/${orcamento.id}`}
                        className="inline-flex items-center px-3 py-1 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
                      >
                        <EyeIcon className="h-4 w-4 mr-1" />
                        Ver
                      </Link>
                      <button
                        onClick={() => handleDarBaixa(orcamento.id)}
                        disabled={submitting === orcamento.id}
                        className="inline-flex items-center px-3 py-1 border border-indigo-600 text-indigo-700 bg-indigo-50 rounded-md text-sm font-medium hover:bg-indigo-100 disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        {submitting === orcamento.id ? (
                          <>
                            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-indigo-600 mr-1"></div>
                            Processando...
                          </>
                        ) : (
                          <>
                            <CheckBadgeIcon className="h-4 w-4 mr-1" />
                            Dar Baixa
                          </>
                        )}
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default DarBaixa;
